using Statiq.Extensions.Diagramming.Web;

namespace Diagramming.Tests.Helpers;

public class MockWebAdapter : IWebAdapter
{
    public Task<HttpContent> Post(string url, string content, Dictionary<string, string>? headers = null, CancellationToken cancellationToken = default)
    {
        return Task.Factory.StartNew<HttpContent>(() =>
        {
            if (url.Contains("/svg", StringComparison.InvariantCultureIgnoreCase))
            {
                return new StringContent("""
                                         <svg width="10pt" height="10pt">
                                            <title>Not a real svg!</title>
                                         </svg>
                                         """);
            }
            
            // binary
            return new ByteArrayContent(Convert.FromBase64String(
                ""));
        }, cancellationToken);
    }
}